package com.example.mastercode.uiimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.mastercode.databinding.ItemCountryBindingimport com.example.mastercode.domain.CountriesDataModelclass CountriesAdapter( list: List<CountriesDataModel>):    RecyclerView.Adapter<CountriesAdapter.CountriesViewHolder>() {    private val  countriesList=list    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CountriesViewHolder {        val layoutInflater=LayoutInflater.from(parent.context)        val itemBinding=ItemCountryBinding.inflate(layoutInflater,parent,false)        return CountriesViewHolder(itemBinding)    }    override fun onBindViewHolder(holder: CountriesViewHolder, position: Int) {        holder.bindData(countriesList[position])    }    override fun getItemCount(): Int {        return countriesList.size    }    inner class CountriesViewHolder( itemView: ItemCountryBinding): RecyclerView.ViewHolder(itemView.root) {        val mItemView=itemView        fun bindData(data: CountriesDataModel) {            mItemView.mDataModel=data        }    }}