package com.example.mastercode.uiimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.example.mastercode.repository.RepositoryCountryimport javax.inject.Injectclass ViewModelCountryFactory @Inject constructor (private val repositoryCountry: RepositoryCountry):ViewModelProvider.Factory {    override fun <T : ViewModel?> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(ViewModelCountries::class.java)) {            return ViewModelCountries(repositoryCountry) as T        }        throw IllegalArgumentException("Unknown ViewModel class")    }}